mapper.select:
		SELECT COUNT(1) FROM
        (
            SELECT count(1) FROM  az_ad where  apptype = '1'

                and earnname =#{earnName}


            GROUP BY dt,earnname
        ) t

SELECT dt, earnname, SUM(bg_pv) AS bgPVs, SUM(bg_uv) AS bgUVs, SUM(dj_pv) AS djPVs, SUM(dj_uv) AS djUVs, SUM(az_pv) AS azPVs, SUM(az_uv) AS azUVs FROM az_ad where apptype = '1'
and earnname =#{earnName}
GROUP BY dt, earnname order by dt desc
LIMIT #{startRow}, #{pageSize}
