mapper.select:
SELECT dt, adver_type AS adType, SUM(bg_pv) AS bgpv, SUM(bg_uv) AS bguv, SUM(dj_pv) AS djpv, SUM(dj_uv) AS djuv FROM freeaddata where apptype = '1'
and adver_type =#{adType}
and version = #{version}
GROUP BY dt,adver_type ORDER BY dt DESC
LIMIT #{startRow}, #{pageSize}

SELECT count(1) FROM
(
    SELECT count(1) FROM freeaddata where apptype = '1'


        and adver_type =#{adType}


        and version = #{version}

    GROUP BY dt, adver_type
) t

SELECT count(1) FROM
(
    SELECT count(1) FROM freeaddata_vn where apptype = '1'


        and version = #{version}

    GROUP BY dt
) t

SELECT dt, SUM(bg_pv) AS bgpv, SUM(bg_uv) AS bguv, SUM(dj_pv) AS djpv, SUM(dj_uv) AS djuv FROM freeaddata_vn where apptype = '1'
and version = #{version}
GROUP BY dt order by dt desc
LIMIT #{startRow}, #{pageSize}
