mapper.select:
SELECT dt, d.meaning AS earnWay, SUM(integral_sum) AS pointSum, SUM(user_num) AS userSum, SUM(integral_num) AS timeSum FROM cxb_integral c JOIN sys_integral_dict d ON c.integral_type = d.code AND d.type = 0
GROUP BY integral_type,dt ORDER BY dt DESC
LIMIT #{startRow}, #{pageSize}

SELECT dt, d.meaning AS speedGrade, ROUND(SUM(integral_sum)*(d.code-1),2) AS pointSum, SUM(user_num) AS userSum, SUM(integral_num) AS timeSum FROM cxb_integral c JOIN sys_integral_dict d ON c.integral_type = d.code AND d.type = 2
GROUP BY d.meaning,dt ORDER BY dt DESC
LIMIT #{startRow}, #{pageSize}

SELECT dt, meaning AS consumeType, SUM(integral_sum) AS pointSum, SUM(user_num) AS userSum, SUM(integral_num) AS timeSum FROM cxb_integral c JOIN sys_integral_dict s ON c.integral_type = s.code AND s.type = 1
GROUP BY meaning,dt ORDER BY dt DESC
LIMIT #{startRow}, #{pageSize}
