mapper.select:
SELECT dt, adver_type AS adType, SUM(bg_pv) AS bgpv, SUM(bg_uv) AS bguv, SUM(dj_pv) AS djpv, SUM(dj_uv) AS djuv FROM freeaddata where apptype = '2'
and adver_type =#{adType}
and version = #{version}
GROUP BY dt,adver_type ORDER BY dt DESC
LIMIT #{startRow}, #{pageSize}

SELECT count(1) FROM
(
    SELECT count(1) FROM freeaddata where apptype = '2'


        and adver_type =#{adType}


        and version = #{version}

    GROUP BY dt, adver_type
) t

SELECT COUNT(1) FROM
(
    SELECT count(1) FROM  az_ad where apptype = '2'

        and earnname =#{earnName}


    GROUP BY dt,earnname
) t

SELECT dt, earnname, SUM(bg_pv) AS bgPVs, SUM(bg_uv) AS bgUVs, SUM(dj_pv) AS djPVs, SUM(dj_uv) AS djUVs, SUM(az_pv) AS azPVs, SUM(az_uv) AS azUVs FROM az_ad where apptype = '2'
and earnname =#{earnName}
GROUP BY dt, earnname order by dt desc
LIMIT #{startRow}, #{pageSize}

SELECT count(1) FROM
(
    SELECT count(1) FROM freeaddata_vn where apptype = '2'


        and version = #{version}

    GROUP BY dt
) t

SELECT dt, SUM(bg_pv) AS bgpv, SUM(bg_uv) AS bguv, SUM(dj_pv) AS djpv, SUM(dj_uv) AS djuv FROM freeaddata_vn where apptype = '2'
and version = #{version}
GROUP BY dt order by dt desc
LIMIT #{startRow}, #{pageSize}
