mapper.select:
SELECT dt, IFNULL(day_number, '其他') AS vipDays, IFNULL(SUM(vip_pv),0) AS timeSum, IFNULL(ROUND(SUM(vip_pv)/(SELECT IFNULL(SUM(vip_pv),0) FROM zwsc_vip_consume where dt = t.dt)*100,2),0) AS timeRate, IFNULL(SUM(vip_uv),0) AS userSum, IFNULL(ROUND(SUM(vip_uv)/(SELECT IFNULL(SUM(vip_uv),0) FROM zwsc_vip_consume where dt = t.dt)*100,2),0) AS userRate, IFNULL(SUM(money) ,0)AS moneySum, IFNULL(ROUND(SUM(money)/(SELECT IFNULL(SUM(money),0) FROM zwsc_vip_consume where dt = t.dt)*100,2),0) AS moneyRate FROM zwsc_vip_consume t
GROUP BY day_number,dt ORDER BY dt DESC
LIMIT #{startRow}, #{pageSize}

SELECT dt, IFNULL(day_number, '其他') AS vipDays, IFNULL(SUM(vip_pv),0) AS timeSum, IFNULL(ROUND(SUM(vip_pv)/(SELECT IFNULL(SUM(vip_pv),0) FROM zwsc_vip_zs_consume where dt = t.dt)*100,2),0) AS timeRate, IFNULL(SUM(vip_uv),0) AS userSum, IFNULL(ROUND(SUM(vip_uv)/(SELECT IFNULL(SUM(vip_uv),0) FROM zwsc_vip_zs_consume where dt = t.dt)*100,2),0) AS userRate FROM zwsc_vip_zs_consume t
GROUP BY day_number,dt ORDER BY dt DESC
LIMIT #{startRow}, #{pageSize}
